import "./ExpenseForm.css";
import { useState } from "react";

const ExpenseForm = ({ onSaveExpenseData, onCancel }) => {
  const [value, setValue] = useState({ title: "", amount: "", date: "" });

  const changeTitleHandler = (event) => {
    setValue((prevValue) => {
      return { ...prevValue, title: event.target.value };
    });
  };

  const changeAmountHandler = (event) => {
    setValue((prevValue) => {
      return { ...prevValue, amount: event.target.value };
    });
  };

  const changeDateHandler = (event) => {
    setValue((prevValue) => {
      return { ...prevValue, date: event.target.value };
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const result = { ...value, date: new Date(value.date) };
    onSaveExpenseData(result);
    setValue({ title: "", amount: "", date: "" });
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={value.title}
            onChange={changeTitleHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={value.amount}
            onChange={changeAmountHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            value={value.date}
            onChange={changeDateHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="button" onClick={onCancel}>
          Cancel
        </button>
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
