const ExpenseDate = ({ date }) => {
  const month = date.toLocaleString('en-US', {month: 'long'} );
  const day = date.toLocaleString('em-US', {day: "2-digt"});
  const year = date.getFullYear();

  return (
    <div>
      <div>{month}</div>
      <div>{day}</div>
      <div>{year}</div>
    </div>
  );
};

export default ExpenseDate;
